INSERT INTO [dbo].[Products] ([ProductID], [Brand], [Name], [Description], [Price], [Category], [ImageMimeType], [ImageName]) VALUES (49, N'IN-TERM', N'Терморегулятор Interm E60 ', N'(In-Term) E-60 – електронний терморегулятор з великим рідкокристалічним дисплеєм. Який має температурний діапазон від +5 до +40С, для керування електричними системами обігріву «тепла підлога», а також опалювальними приладами (настінні конвектори, інфрачервоні стельові панелі). На лицьовій частині термостата крім великого рідкокристалічного дисплея є чотири круглих кнопки, для зручного використання та налаштування бажаної температури. Графічний дисплей має синє підсвічування, і відображає всю необхідну для нашої зручності інформацію: поточний час, режим роботи та температуру в заданому приміщенні.', CAST(608.00 AS Decimal(18, 2)), N'Терморегулятори', N'image/jpeg', N'TER_1.jpg')
INSERT INTO [dbo].[Products] ([ProductID], [Brand], [Name], [Description], [Price], [Category], [ImageMimeType], [ImageName]) VALUES (50, N'IN-TERM', N'Терморегулятор Interm RTC-70', N'Одна з найдоступніших моделей терморегуляторів на ринку України. В комплекті виносний датчик температури підлоги з довжиною підвідного проводу 3 метри. Монтується всередину підрезетника діаметром 67мм. Термостат RTC 70 має дуже просту панель управління. На лицьовій частині регулятора тумблер вимикання та вмикання, а також поворотний ролик з позначенням температури від +0С до +40С. Необхідно просто включити тумблер та виставити роликом бажану температуру підлоги. Терморегулятор сам в автоматичному режимі, буде підтримувати виставлену температуру системи, поки ви його не вимкнете.', CAST(510.00 AS Decimal(18, 2)), N'Терморегулятори', N'image/jpeg', N'TER_2.jpg')
INSERT INTO [dbo].[Products] ([ProductID], [Brand], [Name], [Description], [Price], [Category], [ImageMimeType], [ImageName]) VALUES (51, N'IN-TERM', N'Терморегулятор Interm E-51 ', N'Терморегулятор (In-Term E-51) - програмований терморегулятор з двома датчиками (підлоги та повітря). Програми підтримують тижневий цикл роботи з шістьма періодами часу на кожен день. Температура та діапазон часу кожного періоду налаштовуються під особливості Вашого розпорядку дня. Прилад підтримує як «автоматичний» так і «ручний» режим управління. Дана модель термостата працює паралельно з двома датчиками: виносним датчиком температури підлоги, та вбудованим датчиком температури повітря. Тому може застосовуватися як для системи підігріву підлоги, так і для обігріву приміщення з будь-якими електричними опалювальними приладами. Можливий діапазон регулювання температури від-5С до +90С. Монтаж проводиться в стандартну розподільчу коробку діаметром 60 мм. ', CAST(960.00 AS Decimal(18, 2)), N'Терморегулятори', N'image/jpeg', N'TER_3.jpg')
INSERT INTO [dbo].[Products] ([ProductID], [Brand], [Name], [Description], [Price], [Category], [ImageMimeType], [ImageName]) VALUES (52, N'IN-TERM', N'Терморегулятор Interm E-91 ', N'Терморегулятор (In-Term E-91) - сенсорний програмований терморегулятор з двома датчиками (підлоги та повітря). Програми підтримують тижневий цикл роботи з шістьма періодами часу на кожен день. Температура та діапазон часу кожного періоду налаштовуються під особливості Вашого розпорядку дня. Прилад підтримує як «автоматичний» так і «ручний» режим управління. Дана модель термостата працює паралельно з двома датчиками: виносним датчиком температури підлоги, та вбудованим датчиком температури повітря. Тому може застосовуватися як для системи підігріву підлоги, так і для обігріву приміщення з будь-якими електричними опалювальними приладами. Можливий діапазон регулювання температури від-5С до +90С. Монтаж проводиться в стандартну розподільчу коробку діаметром 60 мм. ', CAST(1242.00 AS Decimal(18, 2)), N'Терморегулятори', N'image/png', N'TER_4.jpg')
